import React, { Component } from 'react';
import NavBar from './NavBar';
import Web3 from 'web3';
import Tether from '../truffle_abis/Tether.json';



class App extends Component {
    // Our React Code Goes In Here!

    async componentDidMount() {
        await this.loadWeb3();
        await this.loadBlockChainData();
    }

    async loadWeb3() {
        if (window.ethereuem) {
            window.web3 = new Web3(window.ethereuem);
            await window.ethereuem.enable();
        } else if (window.web3) {
            window.web3 = new Web3(window.web3.currentProvider)
        } else {
            window.alert('No ethereum browser detected! You cab check out MetaMask!')
        }
    }

    async loadBlockChainData() {
        const web3 = window.web3;
        const account = await web3.eth.requestAccounts();
        this.setState({account: account[0]});
        const networkId = await web3.eth.net.getId();


        //Load Tether Contract
        const tetherData = Tether.networks[networkId];
        if(tetherData) {
            const tether = new web3.eth.Contract(Tether.abi, tetherData.address);
            this.setState({tether: tether});
            let tetherBalance = await tether.methods.balanceOf(this.state.account).call();
            this.setState({tetherBalance: tetherBalance});

            console.log(this.state.tetherBalance);
        }
    }

    constructor(props) {
        super(props);
        this.state = {
            account: '0x0',
            tether: {},
            rwd: {},
            decentralBank: {},
            tetherBalance: '0',
            rwdBalance: '0',
            stakingBalance: '0',
            loading: true
        }
    }

    render() {
        return (
            <div>
                <div>
                    <NavBar account={this.state.account} />
                </div>
                <div>
                    <h1>Hello, World!</h1>
                </div>
            </div>
        )

    }
}

export default App;
